#include<iostream>#include<vector>#include<unordered_map>#include<algorithm>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<ll> a(n);        unordered_map<ll,ll> freq;        for(int i=0;i<n;i++){            cin>>a[i];            freq[a[i]]++;        }        if(freq.size()>2){            cout<<"NO\n";        }        else if(freq.size()==1){            cout<<"YES\n";        }        else{            auto it= freq.begin();            int first_freq= it->second;            it++;            int second_freq= it->second;            if(abs(first_freq-second_freq)<=1){                cout<<"YES\n";            }            else{                cout<<"NO\n";            }        }            }    return 0;}