#include<iostream>#include<vector>#include<algorithm>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,x;        cin>>n>>x;        vector<int>arr(n);        for(int i=0;i<n;i++){            cin>>arr[i];        }        int maximum;        if(n==1){            maximum= max(arr[0],(2*(x-arr[0])));        }        else{            maximum=arr[0];            for(int i=1;i<n;i++){                maximum= max(maximum,(arr[i] - arr[i-1]));            }            maximum= max(maximum, (2 * (x - arr[n-1])));        }                    cout<<maximum<<endl;    }}