#include<iostream>#include<string>using namespace std; int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        string s;        cin>>s;        int n=s.length();        int count_0=0;        int count_1=0;        for(int i=0;i<n;i++){            if(s[i]=='0'){                count_0++;            }            else{                count_1++;            }        }        if(count_0==count_1){            cout<<0<<"\n";            continue;        }        int i=0;                int replaced=0;        do{            if(s[i]=='0' && count_1>0){                count_1--;                replaced++;            }            else if(s[i]=='1' && count_0>0){                count_0--;                replaced++;            }            i++;        }        while(i<n && (count_0>0 && count_1>0) );         while(count_0>0 && i<n && s[i]=='1'){            count_0--;            replaced++;            i++;        }        while(count_1>0 && i<n && s[i]=='0'){            count_1--;            replaced++;            i++;        }         cout<<n- replaced<<"\n";    } }