#include <iostream>#include <vector>using namespace std; void solve() {    int n, k;    cin >> n >> k;        // Using a variable to store consecutive zeros count    // No need to store the whole array if we process on the fly,     // but vector is fine too.    int current_zeros = 0;    int total_hikes = 0;        for (int i = 0; i < n; i++) {        int weather;        cin >> weather;                if (weather == 0) {            current_zeros++;        } else {            // We hit a '1' (Rain). Calculate hikes for the previous block of zeros.            // Formula: (Length + 1) / (k + 1)            total_hikes += (current_zeros + 1) / (k + 1);            current_zeros = 0; // Reset counter        }    }        // IMPORTANT: Process the final block of zeros if the array ended with 0s    if (current_zeros > 0) {        total_hikes += (current_zeros + 1) / (k + 1);    }        cout << total_hikes << endl;} int main() {    ios_base::sync_with_stdio(false);    cin.tie(NULL);        int t;    cin >> t;    while (t--) {        solve();    }    return 0;}