#include<iostream>#include<vector>#include<algorithm>#include<unordered_map>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n ;        cin>>n;        vector<int> arr(n);        unordered_map<int,long long> freq;        for(int i=0;i<n;i++){            cin>>arr[i]; //elements can be -1,0,1            freq[arr[i]]++;        }        long long count=0;        for(auto it:freq){            if(it.first== -1){                if(it.second%2!=0){                    count+=2;                }            }            else if(it.first==0){                    count+=it.second;            }        }        cout<<count<<endl;    }}