#include <iostream>#include <vector>#include <algorithm>using namespace std; int main() {    // Optimize I/O operations    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        int n, m;        cin >> n >> m;                // CHANGE 1: The array size is m, not n        vector<int> arr(m);                // CHANGE 2: Read m elements, not n        for (int i = 0; i < m; i++) {            cin >> arr[i];        }         bool is_strictly_increasing = true;        // This loop is correct now because arr has size m        for (int i = 0; i < m - 1; i++) {            if (arr[i + 1] != arr[i] + 1) {                is_strictly_increasing = false;                break;            }        }         if (is_strictly_increasing) {            int last_ele = arr[m - 1];            cout << (n - last_ele + 1) << endl;        } else {            cout << 1 << endl;        }    }    return 0;}