#include<iostream>#include<vector>#include<unordered_map>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n);        unordered_map<int,int>freq;        //frequency map        for(int i=0;i<n;i++){            cin>>a[i];            freq[a[i]]++;        }        int count=0;                for(auto it:freq){            int x=it.first;            int y=it.second;            if(y<x){                count+=y;            }            else if(y>x){                count+=y-x;            }        }        cout<<count<<endl;    }}