#include<iostream>#include<string>#include<algorithm>#include<cmath>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    long long t;    cin>>t;    while(t--){        long long n,x,y;        cin>>n>>x>>y;        string s;        cin>>s;        long long four=0, eight=0;        for(char c:s){            if(c=='4')four++;            else eight++;        }        long long max_dist= four * 1 + eight * 2;        bool is_black= ((abs(x) + abs(y))<=max_dist);                long long max_cord=n;        bool is_diagnol_black= (max(abs(x),abs(y))<=max_cord);                if(is_black && is_diagnol_black){            cout<<"YES"<<endl;        }        else{            cout<<"NO"<<endl;        }    }}