#include<iostream>#include<algorithm>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n,y,r;        cin>>n>>y>>r;        int suspended=0;        //suspend players with red cards first          suspended+=min(r,n);        n-=suspended;        //suspend players with yellow cards        int no_of_players_with_yellow_cards=y/2;        suspended+=min(no_of_players_with_yellow_cards,n);        cout<<suspended<<endl;    }    return 0;}