#include<iostream>#include<vector>#include<unordered_map>#include<algorithm>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int> a(2*n);        for(int i=0; i<2*n; i++){            cin>>a[i];        }        // count frequency of each element        unordered_map<int, int> freq;        for(int i=0; i<2*n; i++){            freq[a[i]]++;        }        // count distinct elements        int no_of_distinct_ele_with_odd_count = 0;        for(auto it: freq){            if(it.second % 2 != 0){                no_of_distinct_ele_with_odd_count++;            }        }        // calculate answer        int no_of_distinct_ele_with_even_count = freq.size() - no_of_distinct_ele_with_odd_count;        int ans= no_of_distinct_ele_with_odd_count + (no_of_distinct_ele_with_even_count * 2);        // adjust answer based on conditions        if(no_of_distinct_ele_with_odd_count==0){            if(n%2 != no_of_distinct_ele_with_even_count%2){                cout<<ans-2<<endl;            }            else{                cout<<ans<<endl;            }        }        else{            cout<<ans<<endl;        }    }    return 0;}