#include<iostream>#include<vector>#include<algorithm>using namespace std;int main(){    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        int x;        cin>>x;        sort(a.begin(),a.end());        if(x==a[0] || x==a[n-1]){            cout<<"yes"<<endl;        }        else if(x>a[0] && x<a[n-1]){            cout<<"yes"<<endl;        }        else{            cout<<"no"<<endl;        }    }}