#include<iostream>using namespace std;long long get_val(long long rrow,long long ccol,int n){    return (rrow-1)*n + ccol;}void solve() {    long long n;    cin >> n;     if (n == 1) {        cout << 1 << "\n";        return;    }    if(n == 2) {        cout << 9 << "\n"; // Manually computed for 2x2 grid        return;    }     // Candidate A: The best interior cell (N-1, N-1)        long long r = n - 1;    long long c = n - 1;    long long val_A = get_val(r, c, n);    long long cost_A = val_A                      + get_val(r - 1, c, n)  // Up                     + get_val(r + 1, c, n)  // Down                     + get_val(r, c - 1, n)  // Left                     + get_val(r, c + 1, n); // Right     // Candidate B: The best edge cell (N, N-1)        r = n;    c = n - 1;    long long val_B = get_val(r, c, n);    long long cost_B = val_B                      + get_val(r, c - 1, n)  // Left                     + get_val(r, c + 1, n)  // Right                     + get_val(r - 1, c, n); // Up     cout << max(cost_A, cost_B) << "\n";}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        solve();    }    return 0;}