//addition on a segment#include <iostream>#include <vector>#include<algorithm>#include<cmath>#include<numeric>#define ll long longusing namespace std;bool check(ll len,int n,ll sum_b,ll non_zero_count){    if(len>non_zero_count)return false;    ll min_required_sum= len + (n-1);    if(sum_b < min_required_sum)return false;    return true;}Â void solve(){    int n;    cin>>n;    vector<int>b(n);    ll sum_b=0;    ll non_zero_count=0;    for(int i=0;i<n;i++){        cin>>b[i];        sum_b+=b[i];        if(b[i]>0)non_zero_count++;    }    ll low=1;    ll high=n;    ll ans =1;    while(low<=high){        ll mid= low + (high - low)/2;        if(check(mid,n,sum_b,non_zero_count)){            ans=mid; //mid is possible            low=mid+1;// try for a bigger length        }else{            high=mid-1;// mid is too big, try smaller length        }    }    cout<<ans<<"\n";}int main(){    ios_base::sync_with_stdio(false);       cin.tie(NULL);    int t;    cin>>t;    while(t--){        solve();    }    return 0;}