#include <iostream>#include <vector>#include <cmath> using namespace std; void solve() {    int n;    cin >> n;    vector<int> a(n);    for (int i = 0; i < n; i++) {        cin >> a[i];    }     // STEP 1: Fix the boundaries to minimize |a[n-1] - a[0]|    // We prioritize making the difference 0 if possible.        if (a[0] == -1 && a[n - 1] == -1) {        // Both ends are blank. Best difference is 0.        // Lexicographically smallest start is 0.        a[0] = 0;        a[n - 1] = 0;    }     else if (a[0] == -1) {        // Start is blank, End is fixed.        // To minimize difference, Start must equal End.        a[0] = a[n - 1];    }     else if (a[n - 1] == -1) {        // End is blank, Start is fixed.        // To minimize difference, End must equal Start.        a[n - 1] = a[0];    }    // If both are fixed, we do nothing (we can't change the cost).     // STEP 2: Fill all remaining blanks with 0    // Intermediate values do not affect the sum (a[n-1] - a[0]),    // so we make them 0 to ensure the array is lexicographically smallest.    for (int i = 0; i < n; i++) {        if (a[i] == -1) {            a[i] = 0;        }    }     // Output the minimum absolute sum    cout << abs(a[n - 1] - a[0]) << endl;     // Output the array    for (int i = 0; i < n; i++) {        cout << a[i] << (i == n - 1 ? "" : " ");    }    cout << endl;} int main() {    // Fast I/O    ios_base::sync_with_stdio(false);    cin.tie(NULL);     int t;    cin >> t;    while (t--) {        solve();    }    return 0;}