#include <iostream>#include <vector>#include <algorithm> using namespace std; void solve() {    int n;    cin >> n;    vector<int> a(n), b(n);    for (int i = 0; i < n; i++) cin >> a[i];    for (int i = 0; i < n; i++) cin >> b[i];     int diff_count = 0;    int last_diff_index = -1;     // Find differences and the last index where they differ    for (int i = 0; i < n; i++) {        if (a[i] != b[i]) {            diff_count++;            last_diff_index = i + 1; // Store as 1-based index        }    }     // Logic Rule 1: Even number of differences = Tie    if (diff_count % 2 == 0) {        cout << "Tie\n";        return;    }     // Logic Rule 2: Odd number of differences = Last active player wins    // If last_diff_index is Odd, it's Ajisai's turn.    // If last_diff_index is Even, it's Mai's turn.    if (last_diff_index % 2 != 0) {        cout << "Ajisai\n";    } else {        cout << "Mai\n";    }} int main() {    // Optimize I/O operations    ios_base::sync_with_stdio(false);    cin.tie(NULL);        int t;    cin >> t;    while (t--) {        solve();    }    return 0;}