#include<iostream>#include<vector>#include<algorithm>using namespace std;void solve(){    int n;    cin>>n;    if(n==0){        cout<<0<<endl;        return;    }    vector<int>arr(n);    for(int i=0;i<n;i++){        cin>>arr[i];    }        vector<int>remaining_elements;    remaining_elements.push_back(arr[0]);    int current_element=arr[0];    for(int i=1;i<n;i++){        if(arr[i]>=current_element){            remaining_elements.push_back(arr[i]);            current_element=arr[i];        }            }      int removed_count= n - remaining_elements.size();    cout<<removed_count<<endl;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        solve();    }    return 0;}