#include<iostream>using namespace std;// Function to compute GCD of two numbersint gcd(int a, int b) {    if (b == 0)        return a;    return gcd(b, a % b);}int main() {    ios::sync_with_stdio(false);    cin.tie(0);    int t;    cin >> t;    while (t--) {        int l, a, b;        cin >> l >> a >> b;        // Calculate GCD of l and b        // to determine the step size        int g = gcd(l, b);                // Calculate the maximum prize        int max_prize = (l - 1)-((l - 1 -a)%g);        cout << max_prize << "\n";    }    return 0;}