#include <iostream>#include <string> using namespace std; string getMinString(const string &s1, const string &s2) {    return (s1 < s2) ? s1 : s2;}int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;    cin >> t;    while (t--) {        int n;        cin >> n;        string result;        for (int i = 0; i < n; ++i) {            string ai;            cin >> ai;            string option1 = ai + result;            string option2 = result + ai;            result = getMinString(option1, option2);        }        cout << result << '\n';    }     return 0;}