#include <iostream>#include <string>#include <climits>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        string s;        cin>>s;        int ans=INT_MAX;        //check for 2026        for(int i=0;i<=n-4;i++){            int count=0;            if(s[i]!='2') count++;            if(s[i+1]!='0') count++;            if(s[i+2]!='2') count++;            if(s[i+3]!='6') count++;            ans=min(ans,count);        }        //check for 2025        int count2025=0;        for ( int i = 0; i<=n-4; i++)        {            if(s.substr(i,4)=="2025"){                count2025++;                break;            }        }        ans=min(ans,count2025);        cout<<ans<<"\n";Â     }}