#include<iostream>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int a,b;        cin>>a>>b;        int layers=0;        int size=1;        int a1=a,b1=b;        while(true){            if(layers%2==0){                if(a>=size){                    a-=size;                    layers++;                    size*=2;                }                else{                    break;                }            }            else{                if(b>=size){                    b-=size;                    layers++;                    size*=2;                }                else{                    break;                }            }        }                int layers2=0;        int size2=1;Â         while(true){            if(layers2%2==0){                if(b1>=size2){                    b1-=size2;                    layers2++;                    size2*=2;                }                else{                    break;                }            }            else{                if(a1>=size2){                    a1-=size2;                    layers2++;                    size2*=2;                }                else{                    break;                }            }        }        cout<<max(layers,layers2)<<"\n";    }}