#include<iostream>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll s,k,m;        cin>>s>>k>>m;        if(s<=k){            ll time_left = m%k;            cout<<max(0LL, s - time_left)<<"\n";        }        else{            ll time_left= m%(2*k);            if(time_left<k){                cout<<s-time_left<<"\n";            }            else{                cout<<2*k - time_left<<"\n";            }        }    }}