#include<iostream>#include<vector>#include<algorithm>#define ll long longusing namespace std;void solve(){    int n, k;    cin >> n >> k;    if (k == n) {        cout << 0 << "\n";        return;    }    if (k > n) {        cout << -1 << "\n";        return;    }     vector<int> current_piles;    current_piles.push_back(n);     int minutes = 0;        while (true) {        minutes++;        vector<int> next_piles;        bool possible_to_reach_k = false;         for (int x : current_piles) {            int pile1 = x / 2;            int pile2 = (x + 1) / 2;             if (pile1 == k || pile2 == k) {                cout << minutes << "\n";                return;            }             if (pile1 > k) next_piles.push_back(pile1);            if (pile2 > k) next_piles.push_back(pile2);                        if (pile1 >= k || pile2 >= k) possible_to_reach_k = true;        }         sort(next_piles.begin(), next_piles.end());        next_piles.erase(unique(next_piles.begin(), next_piles.end()), next_piles.end());         if (next_piles.empty()) {            cout << -1 << "\n";            return;        }                current_piles = next_piles;    }     }int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){      solve();    }}