#include<iostream>#include<vector>#include<cmath>using namespace std;int smallest_number(int n, int index,int value,vector<bool>&visited){    for(int i=1;i<=n;i++){        if((abs(value-i))%index==0){            if(!visited[i]){                visited[i]=true;                return i;            }        }    }    return -1;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n;        cin>>n;        vector<int>a(n);        a[n-1]=n;        vector<bool>visited(n+1,false);        visited[n]=true;        for(int i=n-1;i>=1;i--){            a[i-1]=smallest_number(n,i, a[i], visited);        }        for(int i=0;i<n;i++){            cout<<a[i]<<" ";        }        cout<<"\n";    }}