#include<iostream>#include<vector>#include<cmath>#include<string>#define ll long longusing namespace std; int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        string s;        cin>>s;        if(n<=2){            cout<<1<<"\n";            continue;        }        int ans=0;         for(char c:s){            if(c=='1') ans++;        }        int last_one=-1;        for(int i=0;i<n;i++){            if(s[i]=='1'){                if(last_one==-1){                    int gap= i-last_one-1;                    ans+= (gap + 1)/3;                    last_one=i;                }                else{                    int gap= i-last_one-1;                    ans+= (gap)/3;                    last_one=i;                }            }        }        if(last_one==-1){            ans+= (n+2)/3;            cout<<ans<<"\n";            continue;        }        else{            int gap= n-last_one-1;            ans+= (gap + 1)/3;        }         cout<<ans<<"\n";    }}