#include<iostream>#include<vector>using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        int n,h,l;        cin>>n>>h>>l;        vector<int> a(n);        int min_dim= min(h,l);        int max_dim= max(h,l);        int count_less_than_min=0;        int count_between=0;        for(int i=0;i<n;i++){            cin>>a[i];            if(a[i]<=min_dim){                count_less_than_min++;            }            else if(a[i]>min_dim && a[i]<=max_dim){                count_between++;            }        }        cout<<min(count_less_than_min,(count_between + count_less_than_min)/2)<<endl;            }}