#include<iostream>#include<vector>#define ll long longusing namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll>p(n);        p[n-1]=1;        ll sum=n*(n+1)/2;        p[0]=0;        ll sum_2=0;        for(int i=1;i<n-1;i++){            p[i]=1 ^ (i + 1);            sum_2+=p[i];        }        p[0] = sum - sum_2 - 1;        for(int i=0;i<n;i++){            cout<<p[i]<<" ";        }        cout<<endl;    }}