#include<iostream>#include<vector> #include<algorithm>#include<climits>#define ll long longusing namespace std;ll getodd(ll n){    while(n%2==0){        n/=2;    }    return n;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);                for(ll i=0;i<n;i++){            cin>>a[i];         }        bool flag=true;        for(int i=0;i<n;i++){           ll current_index= i+1;           if(getodd(a[i])!=getodd(current_index)){               flag=false;               break;           }        }        if(flag){            cout<<"YES\n";        }        else{            cout<<"NO\n";        }            }}