#include<iostream>#include<vector>#include<algorithm>#include<climits>#define ll long longusing namespace std;bool isValid(int a,int b){    if(a==b)return false;    if(a+b ==7)return false;    return true;}int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> arr(n);                for(int i=0;i<n;i++){            cin>>arr[i];                }        int ops=0;        for(int i=0;i<n-1;i++){            if(!isValid(arr[i],arr[i+1])){                ops++;                i++;            }        }        cout<<ops<<"\n";    }}