#include<iostream>#include<vector>#include<algorithm>#define ll long long    using namespace std;int main(){    ios_base::sync_with_stdio(false);    cin.tie(NULL);    int t ;    cin>>t;    while(t--){        ll n;        cin>>n;        vector<ll> a(n);        for(int i=0;i<n;i++){            cin>>a[i];        }        vector<ll> b(n);        if(n==2){            cout<<a[1]<<" "<<a[0]<<"\n";        }        else{            for(int i=1;i<n-1;i++){                b[i]= (a[i+1] -2*a[i] + a[i-1])/2;            }            ll sum_last=0;            for(int i=1;i<n-1;i++){                sum_last+=(ll)i*b[i];            }            b[n-1]= (a[0] - sum_last)/ (n-1);             ll sum_first=0;            for(int i=1;i<n-1;i++){                sum_first+=(ll)(n-i-1)*b[i];            }            b[0]= (a[n-1] - sum_first)/ (n-1);             for(int i=0;i<n;i++){                cout<<b[i]<<" ";            }            cout<<"\n";        }    }}